import React, {Component} from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import './App.css';
import { BrowserRouter as Router, Switch, Route ,withRouter } from 'react-router-dom';
import { AnimatedSwitch } from 'react-router-transition';
import {Container, Row, Col, Navbar} from 'react-bootstrap';
import {TransitionGroup,CSSTransition} from "react-transition-group";
import Home from './Components/Home/Home';
import WAW from './Components/WAW/WAW';
import Works from './Components/Works/Works';
import Exhib from './Components/Exhib/Exhib';
import Contact from './Components/Contact/Contact';
import styled from "styled-components";


class App extends Component {

    constructor(props) {
        super(props);
        this.state= {
            page : '/',

        }

    }

    pageCallback = (page) => {
        this.setState({page: page});
    }

    render() {


        return (
            <Router>
                <Navbar className='color-nav' fixed='top' align="center">
                    <Col><a href='/' className='nav-brand'> Breakfast </a></Col>
                    <Col><a href='/who_are_we' className='nav-link' style={this.state.page.pathname==="/who_are_we" ? {'text-decoration':'line-through underline'} : {}}> Who Are We </a></Col>
                    <Col><a href='/works' className='nav-link' style={this.state.page.pathname==="/works" ? {'text-decoration':'line-through underline'} : {}}> Works </a></Col>
                    <Col><a href='/exhib' className='nav-link' style={this.state.page.pathname==="/exhib" ? {'text-decoration':'line-through underline'} : {}}> Exhibitions </a></Col>
                    <Col><a href='/contact' className='nav-link' style={this.state.page.pathname==="/contact" ? {'text-decoration':'line-through underline'} : {}}> Contacts </a></Col>
                    <Col><a target="_blank" rel="noopener noreferrer" href='https://www.instagram.com/breakfast_paris/' className='nav-link'> Instagram </a></Col>
                </Navbar>



                            <AnimatedSwitch
      atEnter={{ opacity: 0 }}
      atLeave={{ opacity: 0 }}
      atActive={{ opacity: 1 }}
      className="switch-wrapper"
    >
                            <Route exact path='/' render={(props) => <Home {...props} callbackPage={this.pageCallback} />} />
                            <Route path='/who_are_we' render={(props) => <WAW {...props} callbackPage={this.pageCallback} />} />
                            <Route path='/works' render={(props) => <Works {...props} callbackPage={this.pageCallback} />} />
                            <Route path='/exhib' render={(props) => <Exhib {...props} callbackPage={this.pageCallback} />} />
                            <Route path='/contact' render={(props) => <Contact {...props} callbackPage={this.pageCallback} />} />
                </AnimatedSwitch>




            </Router>
        );
    }
}

export default App;
