import React, {Component} from 'react';
import logo from './logo.svg';
import 'bootstrap/dist/css/bootstrap.min.css';
import './App.css';
import { BrowserRouter as Router, Switch, Route, Link } from 'react-router-dom';
import {Container, Row, Col, Navbar,Image} from 'react-bootstrap';
import Home from './Components/Home/Home';
import WAW from './Components/WAW/WAW';
import Works from './Components/Works/Works';
import Exhib from './Components/Exhib/Exhib';
import Contact from './Components/Contact/Contact';

class App extends Component {

    render() {

        return (
            <Router>
                <Navbar className='color-nav' fixed='top' align="center">
                            <Col><a href='/' className='nav-brand'> Breakfast </a></Col>
                            <Col><a href='/who_are_we' className='nav-link' style={{'text-decoration':'line-through'}}> Who Are We </a></Col>
                            <Col><a href='/works' className='nav-link'>Works</a></Col>
                            <Col><a href='/exhib' className='nav-link'>Exhibitions</a></Col>
                            <Col><a href='/contact' className='nav-link'>Contacts</a></Col>
                            <Col><a target="_blank" rel="noopener noreferrer" href='https://www.instagram.com/breakfast_paris/' className='nav-link'>Instagram</a></Col>
                </Navbar>




                <Switch>

                    <Route exact path='/' component={Home} />
                    <Route path='/who_are_we' component={WAW} />
                    <Route path='/works' component={Works} />
                    <Route path='/exhib' component={Exhib} />
                    <Route path='/contact' component={Contact} />

                </Switch>

            </Router>
        );
    }
}

export default App;
