import React, { Component } from 'react';
import SplitText from 'react-pose-text';
import './Home.css';

const charPoses = {
        exit: { opacity: 0, y: 0 },
        enter: {
            opacity: 0.8,
            y: 0,
            delay: ({ charIndex }) => charIndex * 700
        }
    };



class Home extends Component {

    constructor(props){
        super(props);
        props.callbackPage(props.location);
    }

render() {
    return (
        <div>

            <video autoplay="autoplay" loop="loop" muted className="video">
                <source src={require('../../img/acceuilbq.mp4')} type="video/mp4"/>
            </video>
            <div className='caption page' align='center'>
                <SplitText  className="home-title" initialPose="exit" pose="enter" charPoses={charPoses}>Breakfast</SplitText>
                <br></br>


    <div class="titleCont">
        <h1 class="main-title" id="main-title">
            "Here, in the forest,<br></br><span styles="padding-left:100px">dark and deep,</span><br></br><span styles="padding-right:110px">I offer you,</span><br></br><span styles="padding-left:-20px">eternal sleep."</span>
        </h1>
    </div>
    <canvas id="noise" class="noise"></canvas>
  <div class="vignette"></div>


            </div>


            <footer className='home-footer' align="center">@BKF2020 by Stadler Raphael</footer>


        </div>

    );
}
}

export default Home;
